const popup = document.querySelector("#popup");

const gallery = document.querySelector(".gallery");


gallery.addEventListener("click", (e) => {
    if (e.target.tagName === "IMG") {
        const galleryImg = e.target;
        popup.querySelector("source").srcset = galleryImg.srcset;
        popup.querySelector(".popup__img").src = galleryImg.src;
        popup.classList.add("popup__show");
        document.body.classList.add("scroll_off");
    }
})

popup.addEventListener("click", (e) => {
    if (e.target !== popup.querySelector("img")) {
        popup.classList.remove("popup__show");
        document.body.classList.remove("scroll_off");
    }
});

;
const team = document.querySelectorAll(".team__list-item");

team.forEach(team_member => {
    const hover = team_member.querySelector(".team__hover-window");

    team_member.addEventListener("mouseenter", () => {
        hover.classList.add("team__hover-window-show");
    });

    team_member.addEventListener("mouseleave", () => {
        hover.classList.remove("team__hover-window-show");
    });
});


document.querySelector(".form").addEventListener("submit", (e) => {
    e.preventDefault();
    document.querySelectorAll(".form__input")
        .forEach(item => {
            item.value = "";
        });

    alert("Your message was send!");
})