const popup = document.querySelector("#popup");

const gallery = document.querySelectorAll(".gallery__item");

gallery.forEach(galleryItem => {
    galleryItem.addEventListener("click", () => {
        popup.querySelector("source").srcset = galleryItem.querySelector("source").srcset;
        popup.querySelector(".popup__img").src = galleryItem.querySelector("img").src;
        popup.classList.add("popup__show");
        document.body.classList.add("scroll_off");
    })
})

popup.addEventListener("click", (e) => {
    if (e.target !== popup.querySelector("img")) {
        popup.classList.remove("popup__show");
        document.body.classList.remove("scroll_off");
    }
});

;
const team = document.querySelectorAll(".team__list-item");

team.forEach(team_member => {
    const hover = team_member.querySelector(".team__hover-window");

    team_member.addEventListener("mouseenter", () => {
        hover.classList.add("team__hover-window-show");
    });

    team_member.addEventListener("mousemove", () => {

    });

    team_member.addEventListener("mouseleave", () => {
        hover.classList.remove("team__hover-window-show");
    });
});
var myMap;

// Дождёмся загрузки API и готовности DOM.
ymaps.ready(init);

function init() {
    // Создание экземпляра карты и его привязка к контейнеру с
    // заданным id ("map").
    myMap = new ymaps.Map('map', {
        // При инициализации карты обязательно нужно указать
        // её центр и коэффициент масштабирования.
        center: [51.550857, -0.111916],
        zoom: 15
    }, {
        searchControlProvider: 'yandex#search'
    });
};

